import React from "react";
import MessageList from "./messageList";
import MessageForm from "./messageForm";
export default class MessageBox extends React.Component{
    constructor(){
        super();
        this.state = {
            messages:[{id:1,auth:"byp",content:"今天吃药了吗",createAt:Date.now()}]
        }
    }
    addMessage = (message)=>{
        let messageItem = {...message,id:Math.random(),createAt:Date.now()};
        let messages = [...this.state.messages,messageItem];
        this.setState({
           messages:messages
        })
    };
    removeMessage = ()=>{};
    render(){
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-6 col-md-offset-3">
                        <div className="panel panel-danger">
                            <div className="panel-heading">
                                <h1 className="text-center h3">我的留言板</h1>
                            </div>
                            <div className="panel-body">
                                <MessageList {...this.state}/>
                            </div>
                            <div className="panel-footer">
                                <MessageForm add={this.addMessage}/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
